import React from 'react'
import './testimonials.css'
import AVTR1 from '../../assets/avatar1.jpg'
import AVTR2 from '../../assets/avatar2.jpeg'
import AVTR3 from '../../assets/avatar3.jpg'

const data = [
  {
    avatar: AVTR1,
    name: 'William Renald',
    Review: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse accumsan maximus justo, nec tincidunt nibh porttitor vel. Donec volutpat odio nibh, sed maximus mauris porttitor quis. Aenean vehicula posuere enim vel rhoncus'
  },
  {
    avatar: AVTR2,
    name: 'Shane Squilderfeld',
    Review: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse accumsan maximus justo, nec tincidunt nibh porttitor vel. Donec volutpat odio nibh, sed maximus mauris porttitor quis. Aenean vehicula posuere enim vel rhoncus'
  },
  {
    avatar: AVTR3,
    name: 'Anna Sharapovem',
    Review: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse accumsan maximus justo, nec tincidunt nibh porttitor vel. Donec volutpat odio nibh, sed maximus mauris porttitor quis. Aenean vehicula posuere enim vel rhoncus'
  },
]

const testimonials = () => {
  return (
    <section id='testimonials'>
      <h5>Reviews from Clients</h5>
      <h2>Testimonials</h2>
      <div className="container testimonials__container">
        {
          data.map(({avatar, name, review,}, index) => {
            return (
              <article key={index} className="testimonial">
              <div className="client__avatar">
                <img src={avatar} alt="Avatar One"/>
              </div>
              <h5 className='client__name'>{name}</h5>
              <small className='client__review'>{review}</small>
            </article>
            )
          })
        }
      </div>
    </section>
  )
}

export default testimonials
